mapper.select:
SELECT * FROM ( SELECT A.*, ROWNUM AS RN FROM ( SELECT R.REQ_NO, R.REQUEST_DATE, R.REQUEST_TIME, R.STATUS, R.PAY_STATUS, R.CONTENT, R.INSERT_TIME, R.MASTER_NO, MM.NAME AS MASTER_NAME, MM.PROFILE_PHOTO AS MASTER_PROFILE_PHOTO, MM.ADDRESS AS MASTER_ADDRESS, R.CLIENT_NO, MC.NAME AS CLIENT_NAME, MC.PROFILE_PHOTO AS CLIENT_PROFILE_PHOTO, MC.ADDRESS AS CLIENT_ADDRESS, MC.ADDRESS_DETAIL , C.CA_NO , C.CA_NAME , CR.CA_NO AS CA_ROOT_NO , CR.CA_NAME AS CA_ROOT_NAME FROM REQUEST R JOIN MEMBER MC ON R.CLIENT_NO = MC.ME_NO JOIN MEMBER MM ON R.MASTER_NO = MM.ME_NO LEFT JOIN CATEGORY C ON C.CA_NO = MM.CA_NO LEFT JOIN CATEGORY CR ON CR.CA_NO = C.ROOT_NO WHERE 1 = 1
AND MC.ME_NO = #{clientNo}
AND MM.ME_NO = #{masterNo}
ORDER BY ${sortField} ${sortOrder} NULLS LAST ) A ) WHERE RN BETWEEN #{startRow} and #{endRow}

SELECT COUNT(*) FROM REQUEST R JOIN MEMBER MC ON R.CLIENT_NO = MC.ME_NO JOIN MEMBER MM ON R.MASTER_NO = MM.ME_NO WHERE 1 = 1
AND MC.ME_NO = #{clientNo}
AND MM.ME_NO = #{masterNo}

mapper.update:
UPDATE REQUEST SET
PAY_STATUS = #{payStatus},
UPDATE_TIME = SYSDATE WHERE REQ_NO = #{reqNo}
